function ParticleWave(t,s,i){this.mX=t,this.mY=s,this.mRadius=i,this.waveSpectrum=function(){return this.spectrum=fft.analyze(),this.spectrum},this.show=function(){beginShape(),stroke(255,0,0);for(var t=PI;t<TWO_PI;t+=.1){for(var s=0;s<spectrum.length;s++)var e=spectrum[s];var r=map(e,0,256,0,400),n=i+r,a=n*cos(t)*2,h=n*sin(t);vertex(a,h)}endShape()},this.position=function(){translate(this.X,this.Y)}}