function toggleSong(){song.isPlaying()?song.pause():song.play()}function preload(){song=loadSound("Atmosphere-Sunshine.mp3")}function setup(){createCanvas(window.innerWidth,window.innerHeight).parent("visualizer"),fft=new p5.FFT(.95,64);var e=color("rgba(22, 25, 37, .85)"),n=color("rgb(142, 137, 137)"),o=color("rgb(106, 63, 190)"),a=color("rgb(200, 109, 215)");wave=new CurveWave(o,1.2),wave2=new CurveWave(n,2),wave3=new CurveWave(e,4),button=createButton("toggle"),button.mousePressed(toggleSong),song.play()}function windowResized(){resizeCanvas(window.innerWidth,window.innerHeight)}function draw(){background(0),wave.show(),wave2.show(),wave3.show()}var song,fft,button,radius,wave,wave2,wave3;