function toggleSong(){song.isPlaying()?song.pause():song.play()}function preload(){song=loadSound("Atmosphere-Sunshine.mp3")}function setup(){createCanvas(window.innerWidth,window.innerHeight).parent("visualizer"),fft=new p5.FFT,fft.smooth(.95);var e=color("rgba(11, 10, 10, .65)"),n=color("rgba(142, 137, 137, .65)"),a=color("rgb(106, 63, 190)");wave=new CurveWave(a,4),wave2=new CurveWave(n,3),wave3=new CurveWave(e,2),button=createButton("toggle"),button.mousePressed(toggleSong),song.play()}function windowResized(){resizeCanvas(window.innerWidth,window.innerHeight)}function amplitude(){for(var e=fft.analyze(),n=0;n<e.length;n++){var a=e[n],t=map(a,0,256,400,0),o=map(a,0,256,200,300),r=map(a,0,256,200,300);return[t,o,map(a,0,256,400,200)]}}function draw(){background(255),translate(width/2,height/2),beginShape(),vertex(493,396),vertex(10,398),vertex(10,300),bezierVertex(amplitude()[0],0,amplitude()[2],amplitude()[1],493,300),endShape()}var song,fft,button,radius,wave,wave2,wave3;