function Wave(t,i,s,h,e){this.mColor=t,this.mPosX=i,this.mPosY=s,this.mLimiter=h,this.mFlipWave=e,this.yoff=0,this.waveSpectrum=function(){return this.spectrum=fft.analyze(),this.spectrum},this.show=function(t){beginShape(),stroke(this.mColor),strokeWeight(10),smooth(),this.position(),this.waveSpectrum(),this.amplitude(),endShape()},this.position=function(){translate(this.mPosX,this.mPosY)},this.amplitude=function(){for(var t=0;t<this.waveSpectrum().length;t++)this.amp=this.waveSpectrum()[t],this.y=map(this.amp/this.mLimiter,0,256,height,0),line(50*t,2*height,50*t,this.y);return this.amp}}