function Wave(t,s,i,h,e){this.mColor=t,this.mPosX=s,this.mPosY=i,this.mLimiter=h,this.mFlipWave=e,this.yoff=0,this.waveSpectrum=function(){return this.spectrum=fft.analyze(),this.spectrum},this.show=function(t){this.radius=150,beginShape();for(var s=0;s<TWO_PI;s+=.1)this.offset=map(t,0,256,-25,25),this.r=this.radius+this.offset,this.x=this.r*cos(s),this.y=this.r*sin(s),vertex(this.x,this.y);endShape()},this.position=function(){translate(this.mPosX,this.mPosY)},this.amplitude=function(){for(var t=0;t<this.waveSpectrum().length;t++)this.amp=this.waveSpectrum()[t],show(amp);return this.amp}}